import { getDifficulty } from '../utils';
import {isValid} from './../schemas';
import {trymore} from 'crypto-io-utils';
import {read} from 'crypto-io-fs';
import events from './bus';
import {validateTransactions, createRewardTransaction} from './transaction';
import {calculateBlockHash} from './hash';
import {BlockError, TransactionError} from './errors.js';


/**
 * validate block
 *
 * @param {object} previousBlock
 * @param {object} block
 * @param {number} difficulty
 * @param {number} unspent
 */
export const validateBlock = (previousBlock, block, difficulty, unspent) => {
	if (!isValid('block', block)) throw BlockError('data');
	if (previousBlock.index + 1 !== block.index) throw BlockError('index');
	if (previousBlock.hash !== block.prevHash) throw BlockError('prevhash');
	if (calculateBlockHash(block) !== block.hash) throw BlockError('hash');
	if (blockDifficulty(block) > difficulty) throw BlockError('difficulty');
	return validateTransactions(block.transactions, unspent);
};

/**
 * Create genesis block
 *
 * @return {object} {index, prevHash, time, transactions, nonce}
 */
export const genesisBlock = () => {
	const block = {
		index: 0,
		prevHash: '0',
		time: Math.floor(new Date().getTime() / 1000),
		transactions: [],
		nonce: 0
	};
	block.hash = calculateBlockHash(block);
	return block;
};

/**
 * Get hash difficulty
 *
 * @param {*} hash
 * @return {number}
 */
export const difficulty = (hash) => {
	return parseInt(hash.substring(0, 8), 16);
};

/**
 * Get hash difficulty from block
 *
 * @param {*} hash
 * @return {number}
 */
export const blockDifficulty = ({hash}) => {
	return difficulty(hash);
};

/**
 * Create new block
 *
 * @param transactions {array}
 * @param lastBlock {object}
 * @param address {string}
 * @return {index, prevHash, time, transactions, nonce}
 */
export const block = (transactions, lastBlock, address) => {
	transactions = transactions.slice();
	transactions.push(createRewardTransaction(address, lastBlock.index + 1));
	const block = {
		index: lastBlock.index + 1,
		prevHash: lastBlock.hash,
		time: Math.floor(new Date().getTime() / 1000),
		transactions,
		nonce: 0,
	};
	block.hash = calculateBlockHash(block);

	return block;
};
export default block;

import { DAGNode } from 'ipld-dag-pb';
import { validateTransactions, createRewardTransaction } from './../transaction';
import { hexFromMultihash, getDifficulty } from './../../utils';
import { isValid } from 'crypto-chain-validator';
import { BlockError } from '../errors';
import IPFS from 'ipfs-api';
const ipfs = new IPFS();

const calculateHash = async block => {
  block = await createDAGNode(block);
  return block.multihash.toString('hex').substring(4);
}

const createDAGNode = ({index, prevHash, time, transactions, nonce}) => {
  return new Promise((resolve, reject) => {
    DAGNode.create(JSON.stringify({
      index,
      prevHash,
      time,
      transactions,
      nonce
    }), [], 'sha2-256', (error, dagNode) => {
      if (error) {
        return reject(error);
      }
      return resolve(dagNode)
    });
  });
}

/**
 * Create new block
 *
 * @param transactions {array}
 * @param previousBlock {object}
 * @param address {string}
 * @return {index, prevHash, time, transactions, nonce}
 */
class DAGBlock {
	constructor(options) {
    if (!options) return;
		if (typeof options === 'object' && !Buffer.isBuffer(options)) return this.newBlock(options);
		else return this.get(options);
	}

	async newBlock({transactions, previousBlock, address}) {
		transactions = transactions.slice();
		transactions.push(createRewardTransaction(address, previousBlock.index + 1));
		this.data = {
			index: previousBlock.index + 1,
			prevHash: previousBlock.hash,
			time: Math.floor(new Date().getTime() / 1000),
			transactions,
			nonce: 0
		};
		this.data.hash = await calculateHash(this.data);
		return this.data;
	}
	transformBlock({multihash, data}) {
	  data = JSON.parse(data.toString());
	  data.hash = hexFromMultihash(multihash);
	  return data;
	};
	async get(hash) {
		this.node = await ipfs.object.get(hash);
		return this.transformBlock(this.node);
	}
	async put(block) {
		this.node = await createDAGNode(block || this.data);
		return await ipfs.object.put(this.node);
	}
}

/**
 * validate block
 *
 * @param {object} previousBlock
 * @param {object} block
 * @param {number} difficulty
 * @param {number} unspent
 */
const validate = async (previousBlock, block, difficulty, unspent) => {
	if (!isValid('block', block)) throw BlockError('data');
	if (previousBlock.index + 1 !== block.index) throw BlockError('index');
	if (previousBlock.hash !== block.prevHash) throw BlockError('prevhash');
	if (await calculateHash(block) !== block.hash) throw BlockError('hash');
	if (getDifficulty(block.hash) > difficulty) throw BlockError('difficulty');
	return validateTransactions(block.transactions, unspent);
};

export {
  DAGBlock,
  validate,
  calculateHash,
  createDAGNode
};

import { config, median, multihashFromHex } from './../../utils';
import { validateTransaction } from './../transaction.js';
import { TransactionError } from './../errors.js';
import { DAGBlock, createDAGNode, validate } from './dagblock';
import { encode, decode } from 'bs58';
import { GENESISBLOCK } from '../../params';
import { resolvePeers } from '../network/peernet';
import IPFS from 'ipfs-api';
const ipfs = new IPFS();

global.chain = global.chain || [
  GENESISBLOCK
];

export const chain = (() => global.chain)();

export const mempool = (() => [])();

/**
 * Get the transactions for the next Block
 *
 * @return {object} transactions
 */
export const nextBlockTransactions = () => {
	const unspent = getUnspent(false);
	return mempool.filter((transaction) => {
		try {
			return validateTransaction(transaction, unspent);
		} catch (e) {
			if (! (e instanceof TransactionError)) throw e;
		}
	});
};

export const getTransactions = (withMempool = true) => {
	let transactions = chain.reduce((transactions, block) => transactions.concat(block.transactions), []);
	if (withMempool) transactions = transactions.concat(mempool);
	return transactions;
};

export const getTransactionsForAddress = address => {
	return getTransactions(false).filter(tx => tx.inputs.find(i => i.address === address) ||
  tx.outputs.find(o => o.address === address));
};

export const getUnspent = (withMempool = false) => {
	const transactions = getTransactions(withMempool);
	// Find all inputs with their tx ids
	const inputs = transactions.reduce((inputs, tx) => inputs.concat(tx.inputs), []);

	// Find all outputs with their tx ids
	const outputs = transactions.reduce((outputs, tx) =>
		outputs.concat(tx.outputs.map(output => Object.assign({}, output, {tx: tx.id}))), []);

	// Figure out which outputs are unspent
	const unspent = outputs.filter(output =>
		typeof inputs.find(input => input.tx === output.tx && input.index === output.index && input.amount === output.amount) === 'undefined');
	return unspent;
};
export const getUnspentForAddress = address => {
	return getUnspent(true).filter(u => u.address === address);
};
export const getBalanceForAddress = address => {
	return getUnspentForAddress(address).reduce((acc, u) => acc + u.amount , 0);
};
export const difficulty = () => {
	// TODO: lower difficulty when transactionpool contain more then 500 tx ?
	// TODO: raise difficulty when pool is empty
	// get the last 128 blocks
	const start = chain.length >= 128 ? (chain.length - 128) : 0;
	const blocks = chain.slice(start, (chain.length - 1)).reverse();
	const stamps = [];
	for (var i = 0; i < blocks.length; i++) {
		if (blocks[i + 1]) {
			stamps.push(blocks[i].time - blocks[i + 1].time);
		}
	}
	if (stamps.length === 0) {
		stamps.push(10);
	}
	let blocksMedian = median(stamps) || 10;
  const offset = blocksMedian / 10
   // offset for quick recovery
	if (blocksMedian < 9) {
		blocksMedian -= offset;
	} else if (blocksMedian > 11) {
		blocksMedian += offset;
	}
  console.log(`Average Block Time: ${blocksMedian}`);
  console.log(`Difficulty: ${10 / blocksMedian}`);
	return 10000 / (10 / blocksMedian); // should result in a block every 10 seconds
};

export const transformBlock = ({multihash, data}) => {
  data = JSON.parse(data.toString());
  data.hash = hashFromMultihash(multihash);
  return data;
};

// TODO: global peerlist
export const longestChain = () => new Promise(async (resolve, reject) => {
  try {
    const peers = await resolvePeers();
    // transform peers into valid ipfs addresses
    const addrs = peers.map(({addr, peer}) => {
      return {
        addr: addr.toString(),
        id: peer.toB58String()
      }
    });
    // peers.push(id);
    const stats = [];
    for (const { addr, id } of addrs) {
      try {
        await ipfs.swarm.connect(`${addr.toString()}/ipfs/${id}`);
        const ref = await ipfs.name.resolve(id);
        const hash = ref.replace('/ipfs/', '');
        // get chain stats for every peer
        const stat = await ipfs.object.stat(hash);
        // push chain length & hash
        stats.push({height: stat.NumLinks - 1, hash: stat.Hash});
      } catch (e) {
        if (e.code === 'ECONNREFUSED') {
          reject(e);
        }
        console.log(`Ignoring ${id}`)
      }
    }
    // reduce to longest chain
    // TODO: consider using canditates for validating
    // canditates.push({hash, height})
    // if c.height > p.height => newCanditatesSet ...
    const stat = stats.reduce((p, c) => {
      if (c.height > p.height || c.height === p.height) return c;
      else return p;
    }, {height: 0, hash: null});
    resolve(stat);
  } catch (e) {
    reject(e);
  }
});

export const lastBlock = async () => {
  const { hash, height } = await longestChain();
  const { links } = await ipfs.object.get(hash); // retrieve links
  // TODO: syncChain if needed
  links.sort((a,b) => a.name - b.name); // sort index
  // get block using its ipfs ref
  return await new DAGBlock(encode(links[height].multihash));
};

export const nextBlock = async address => {
  const transactions = nextBlockTransactions();
  const previousBlock = chain[chain.length - 1];
  return await new DAGBlock({transactions, previousBlock, address});
};

/**
 * Create a new genesis block
 */
export const newGenesisDAGNode = async difficulty => {
  let dagnode;
  const block = {
    index: 0,
    prevHash: '0',
    time: Math.floor(new Date().getTime() / 1000),
    transactions: [],
    nonce: 0
  };

  dagnode = await createDAGNode(block);
  block.hash = dagnode.multihash.toString('hex').substring(4);
  while (parseInt(block.hash.substring(0, 8), 16) >= difficulty) {
    block.nonce++;
    dagnode = await createDAGNode(block);
    block.hash = dagnode.multihash.toString('hex').substring(4);
  }
  return dagnode;
}

import { DAGNode } from 'ipld-dag-pb';
import { decode, encode } from 'bs58';
import EventEmitter from 'events';
import { chain, difficulty, getUnspent, longestChain, lastBlock, newGenesisDAGNode } from './dagchain-interface';
import { DAGBlock, createDAGNode, validate } from './dagblock';
import { read, write } from 'crypto-io-fs';
import { join } from 'path';
import { genesis, network, genesisCID, localIndex, localCurrent, localDAGAddress, localDAGMultiaddress, networks } from './../../params';
import { debug, hashFromMultihash, multihashFromHex, succes, log } from './../../utils';
import bus from './../bus';
