import networks from '../lib/networks';
import { generateMnemonic, mnemonicToSeed } from 'bip39';
import { createHmac } from 'crypto';
import { pointFromScalar } from 'tiny-secp256k1';
const PARENT_FINGERPRINT = 0x00000000;
/**
 * @example
 * validateNetwork('name')
 * validateNetwork({ name, wif, bip32, messagePrefix })
 * validateNetwork({ wif, bip32, messagePrefix }, 'name')
 */
const validateNetwork = (network, name) => {
	if (!network) throw new ReferenceError('network undefined');
	if (typeof network === 'object') {
		if (!network.name && name) network.name = name;
		else throw ReferenceError('network name undefined');
	} else {
		name = network;
		network = networks[network];
		network.name = name;
	}
	if (!network.bip32 || !network.wif)
		throw new ReferenceError(
			`magic ${network.bip32 ? 'wif': 'bip32'} prefix undefined`
		);
	if (!network.bip32.public || !network.bip32.private)
		throw new ReferenceError(
			`magic ${network.bip32.public ? 'bip32.private': 'bip32.public'} prefix undefined`
		);
	if (!network.messagePrefix || !network.multiCodec)
		throw new ReferenceError(
			`magic ${network.multiCodec ? 'messagePrefix' : 'multiCodec'} undefined`
		);
	return network;
};

class BIP32Interface {
	/**
   * @param {object} options
   * @example
   * new BIP32Interface()
   * new BIP32Interface({chainCode, masterKey, network})
   * new BIP32Interface({publicKey})
   */
	constructor(options) {
	}

	fromSeed(seed, network) {
  	if (seed.length < 16) throw new TypeError('Seed should be at least 128 bits');
  	if (seed.length > 64) throw new TypeError('Seed should be at most 512 bits');
  	if (!Buffer.isBuffer(seed)) {
  		seed = Uint8Array(Buffer.from(seed));
  	}
  	try {
  		const network = validateNetwork(network);
  		const hash = createHmac('sha512', network.multiCodec).update(seed).digest();
			const masterKey = hash.slice(0, 32);
			return {
				index: 0,
				depth: 0,
				parentFingerprint: PARENT_FINGERPRINT,
				chainCode: hash.slice(32),
				publicKey: pointFromScalar(masterKey, true),
				masterKey,
				network
			};
  	} catch (e) {
  		throw e;
  	}
	}

	infoFromBuffer(buffer) {
		return {
			version: buffer.readUInt32BE(0),
			depth: buffer[4],
			index: buffer.readUInt32BE(9),
			chainCode: buffer.slice(13, 45),
			parentFingerprint: buffer.readUInt32BE(5)
		};
	}

	fromBase58(base58, network) {
		if (!base58) throw ReferenceError('base58 undefined');
		try {
			const network = validateNetwork(network);
			if (buffer.length !== 78) throw Error('Invalid buffer length');
			const { bip32 } = network;
			const {
				version, index, depth, parentFingerprint, chainCode
			} = this.infoFromBuffer(buffer);
			// validate
			if (version !== bip32.private && version !== bip32.public)
				throw TypeError('invalid network version');

			if (depth === 0 && parentFingerprint !== PARENT_FINGERPRINT)
				throw TypeError('Invalid parent fingerprint');

			if (depth === 0 && index !== 0)
				throw TypeError('Invalid index');

			if (version === bip32.private) {
				if (buffer.readUInt8(45) !== 0x00)
					throw TypeError('Invalid private key');

				const masterKey = buffer.slice(46, 78);
				// cleanup unrelated key
				delete this.publicKey;
				this._setProperties({
					masterKey,chainCode, network, depth, index, parentFingerprint
				});
			} else {
				const publicKey = buffer.slice(45, 78);
				// cleanup unrelated key
				delete this.masterKey;
				this._setProperties({
					publicKey, chainCode, network, depth, index, parentFingerprint
				});
			}
		} catch (e) {
			throw e;
		}
	}
	// Buffer
	// version 0x0488b21f
	// depth 0x00 || 0x01 ...
	// index

	toBase58({}) {
		const version = this.masterKey ? this.network.bip32.private : this.network.bip32.public;
		// this.multiHD({
		//   this.masterKey,
		//   this.index,
		//   this.depth,
		//   this.parentFingerprint,
		//   publicKey: this.masterKey ? this.publicKey : null,
		//   version
		// })

		const buffer = Buffer.allocUnsafe(78);
		// update buffer with version > depth > parentFingerprint >
		buffer.writeUInt32(version, 0);
		buffer.writeUInt8(this.depth, 4);
		buffer.writeUInt32BE(this.parentFingerprint, 5);
		buffer.writeUInt32BE(this.index, 9);

		// 32 bytes: the chain code
		this.chainCode.copy(buffer, 13);
		if (this.masterKey) {
			buffer.writeUInt8(0, 45);
			this.masterKey.copy(buffer, 46);
		} else {
			this.publicKey.copy(buffer, 45);
		}
		return encode(buffer);
	}
}

