import * as IPFS from 'ipfs-api';

export class DynamicSeed {
	constructor(name, ipfs) {
		if (!this.ipfs) {
  		this.ipfs = new IPFS();
  	}
		this.name = name;
	}

	async getSeeds() {
  	try {
      const resolved = await this.ipfs.name.resolve(name);
      const seeds = await this.ipfs.object.get(resolved.replace('/ipfs/', ''));
      return seeds;
    } catch (e) {
      console.warn(e);
    }
	}

	async addSeed(seed) {
    const seeds = await this.getSeeds();
    JSON.parse(seeds.data.toString());
	}

  publish(seeds) {
  	const published = this.ipfs.name.publish(seeds);
  }
}

export default (name, ipfs) => new DynamicSeed(name, ipfs);

export const twelveHours = 43200000;
export default (published, ipfs, cb) => {
  if (!ipfs) {
    const IPFS = require('ipfs-api');
    ipfs = new IPFS();
  }
	const aliveTimeout = () => {
    setTimeout(async () => {
      published = await ipfs.name.resolve(published['name']);
      published = await ipfs.name.publish(published['value']);
      if (cb) {
        cb(null, published);
      }
      return aliveTimeout();
		}, twelveHours);
  }
  aliveTimeout();
};

