import { validateTransactions, createRewardTransaction } from './../transaction';
import { hexFromMultihash, getDifficulty } from './../../utils';
import { isValid } from 'crypto-chain-validator';
import { BlockError } from '../errors';
import createDAGNode from  './dagnode';
import calculateHash from  './calculate-hash';
import IPFS from 'ipfs-api';
const ipfs = new IPFS();

/**
 * Create new block
 *
 * @param transactions {array}
 * @param previousBlock {object}
 * @param address {string}
 * @return {index, prevHash, time, transactions, nonce}
 */
class DAGBlock {
	constructor(options) {
    if (!options) return;
		if (typeof options === 'object' && !Buffer.isBuffer(options)) return this.newBlock(options);
		else return this.get(options);
	}

	async newBlock({transactions, previousBlock, address}) {
		transactions.push(createRewardTransaction(address, previousBlock.index + 1));
		this.data = {
			index: previousBlock.index + 1,
			prevHash: previousBlock.hash,
			time: Math.floor(new Date().getTime() / 1000),
			transactions,
			nonce: 0
		};
		this.data.hash = await calculateHash(this.data);
		return this.data;
	}
	transformBlock({multihash, data}) {
	  data = JSON.parse(data.toString());
	  data.hash = hexFromMultihash(multihash);
	  return data;
	};
	async get(hash) {
		this.node = await ipfs.object.get(hash);
		return this.transformBlock(this.node);
	}
	async put(block) {
		this.node = await createDAGNode(block || this.data);
		return await ipfs.object.put(this.node);
	}
}

/**
 * validate block
 *
 * @param {object} previousBlock
 * @param {object} block
 * @param {number} difficulty
 * @param {number} unspent
 */
const validate = async (previousBlock, block, difficulty, unspent) => {
	if (!isValid('block', block)) throw BlockError('data');
	if (previousBlock.index + 1 !== block.index) throw BlockError('index');
	if (previousBlock.hash !== block.prevHash) throw BlockError('prevhash');
	if (await calculateHash(block) !== block.hash) throw BlockError('hash');
	if (getDifficulty(block.hash) > difficulty) throw BlockError('difficulty');
	return validateTransactions(block.transactions, unspent);
};

export {
  DAGBlock,
  validate
};

import { config, median, multihashFromHex } from './../../utils';
import { validateTransaction } from './../transaction.js';
import { TransactionError } from './../errors.js';
import { DAGBlock, validate } from './dagblock';
import createDAGNode from './dagnode';
import { encode, decode } from 'bs58';
import { GENESISBLOCK } from '../../params';
import { resolvePeers } from '../network/peernet';
import IPFS from 'ipfs-api';
const ipfs = new IPFS();
const invalidTransactions = {};

global.chain = global.chain || [
  GENESISBLOCK
];
global.mempool = global.mempool || [];

export const chain = (() => global.chain)();

export const mempool = (() => global.mempool)();

// TODO: needs 3 nodes running
const invalidTransaction = data => {
  data = JSON.parse(data.toString());
  const tx = invalidTransactions[data.tx];
  let {count} = tx.count || 0;
  if (count === 3) {
    const memIndex = mempool.indexOf(data)
    mempool.splice(memIndex)
    delete invalidTransactions[data.tx];
  } else {
    invalidTransactions[data.tx].count++;
  }
}
ipfs.pubsub.subscribe('invalid-transaction', invalidTransaction)

/**
 * Get the transactions for the next Block
 *
 * @return {object} transactions
 */
export const nextBlockTransactions = () => {
	const unspent = getUnspent(false);
	return mempool.filter(async (transaction) => {
		try {
			return validateTransaction(transaction, unspent);
		} catch (e) {
      // TODO: push to pubus
      ipfs.pubsub.publish('invalid-transaction', new Buffer.from(JSON.stringify(transaction)));

      // TODO: notification... here or in errors propably best here...
			console.error(e);
		}
	});
};

export const getTransactions = (withMempool = true) => {
	let transactions = chain.reduce((transactions, block) => transactions.concat(block.transactions), []);
	if (withMempool) transactions = transactions.concat(mempool);
	return transactions;
};

export const getTransactionsForAddress = address => {
	return getTransactions(false).filter(tx => tx.inputs.find(i => i.address === address) ||
  tx.outputs.find(o => o.address === address));
};

export const getUnspent = (withMempool = false) => {
	const transactions = getTransactions(withMempool);
	// Find all inputs with their tx ids
	const inputs = transactions.reduce((inputs, tx) => inputs.concat(tx.inputs), []);

	// Find all outputs with their tx ids
	const outputs = transactions.reduce((outputs, tx) =>
		outputs.concat(tx.outputs.map(output => Object.assign({}, output, {tx: tx.id}))), []);

	// Figure out which outputs are unspent
	const unspent = outputs.filter(output =>
		typeof inputs.find(input => input.tx === output.tx && input.index === output.index && input.amount === output.amount) === 'undefined');
	return unspent;
};
export const getUnspentForAddress = address => {
	return getUnspent(true).filter(u => u.address === address);
};
export const getBalanceForAddress = address => {
	return getUnspentForAddress(address).reduce((acc, u) => acc + u.amount , 0);
};
export const difficulty = () => {
	// TODO: lower difficulty when transactionpool contain more then 500 tx ?
	// TODO: raise difficulty when pool is empty
	// get the last 128 blocks
	const start = chain.length >= 128 ? (chain.length - 128) : 0;
	const blocks = chain.slice(start, (chain.length - 1)).reverse();
	const stamps = [];
	for (var i = 0; i < blocks.length; i++) {
		if (blocks[i + 1]) {
			stamps.push(blocks[i].time - blocks[i + 1].time);
		}
	}
	if (stamps.length === 0) {
		stamps.push(10);
	}
	let blocksMedian = median(stamps) || 10;
  const offset = blocksMedian / 10
   // offset for quick recovery
	if (blocksMedian < 9) {
		blocksMedian -= offset;
	} else if (blocksMedian > 11) {
		blocksMedian += offset;
	}
  console.log(`Average Block Time: ${blocksMedian}`);
  console.log(`Difficulty: ${10 / blocksMedian}`);
	return 10000 / (10 / blocksMedian); // should result in a block every 10 seconds
};

export const transformBlock = ({multihash, data}) => {
  data = JSON.parse(data.toString());
  data.hash = hashFromMultihash(multihash);
  return data;
};

// TODO: global peerlist
export const longestChain = () => new Promise(async (resolve, reject) => {
  try {
    const peers = await resolvePeers();
    // transform peers into valid ipfs addresses
    const addrs = peers.map(({addr, peer}) => {
      return {
        addr: addr.toString(),
        id: peer.toB58String()
      }
    });
    // peers.push(id);
    const stats = [];
    for (const { addr, id } of addrs) {
      try {
        await ipfs.swarm.connect(`${addr.toString()}/ipfs/${id}`);
        const ref = await ipfs.name.resolve(id);
        const hash = ref.replace('/ipfs/', '');
        // get chain stats for every peer
        const stat = await ipfs.object.stat(hash);
        // push chain length & hash
        stats.push({height: stat.NumLinks - 1, hash: stat.Hash});
      } catch (e) {
        if (e.code === 'ECONNREFUSED') {
          reject(e);
        }
        console.log(`Ignoring ${id}`)
      }
    }
    // reduce to longest chain
    // TODO: consider using canditates for validating
    // canditates.push({hash, height})
    // if c.height > p.height => newCanditatesSet ...
    const stat = stats.reduce((p, c) => {
      if (c.height > p.height || c.height === p.height) return c;
      else return p;
    }, {height: 0, hash: null});
    resolve(stat);
  } catch (e) {
    reject(e);
  }
});

export const lastBlock = async () => {
  const { hash, height } = await longestChain();
  const { links } = await ipfs.object.get(hash); // retrieve links
  // TODO: syncChain if needed
  links.sort((a,b) => a.name - b.name); // sort index
  // get block using its ipfs ref
  return await new DAGBlock(encode(links[height].multihash));
};

export const nextBlock = async address => {
  let transactions;
  let previousBlock;
  try {
    previousBlock = chain[chain.length - 1]; // TODO: await lastBlock
    transactions = await nextBlockTransactions();
  } catch (e) {
    transactions = [];
  } finally {
    return await new DAGBlock({transactions, previousBlock, address});
  }
};

/**
 * Create a new genesis block
 */
export const newGenesisDAGNode = async difficulty => {
  let dagnode;
  const block = {
    index: 0,
    prevHash: '0',
    time: Math.floor(new Date().getTime() / 1000),
    transactions: [],
    nonce: 0
  };

  dagnode = await createDAGNode(block);
  block.hash = dagnode.multihash.toString('hex').substring(4);
  while (parseInt(block.hash.substring(0, 8), 16) >= difficulty) {
    block.nonce++;
    dagnode = await createDAGNode(block);
    block.hash = dagnode.multihash.toString('hex').substring(4);
  }
  return dagnode;
}

import { DAGNode } from 'ipld-dag-pb';
import { decode, encode } from 'bs58';
import EventEmitter from 'events';
import { chain, difficulty, getUnspent, longestChain, lastBlock, newGenesisDAGNode } from './dagchain-interface';
import { DAGBlock, createDAGNode, validate } from './dagblock';
import { read, write } from 'crypto-io-fs';
import { join } from 'path';
import { genesis, network, genesisCID, localIndex, localCurrent, localDAGAddress, localDAGMultiaddress, networks } from './../../params';
import { debug, hashFromMultihash, multihashFromHex, succes, log } from './../../utils';
import bus from './../bus';
// import ipfsKeepAlive from './../../ipfs-keep-alive';
// import bitswap from 'ipfs-bitswap';
// TODO: implement bitswap
// export const ipfs = (() => new IPFS())();

export class DAGChain extends EventEmitter {
  get link() {
    return this.name.replace('/ipfs/', '')
  }
  get links() {
    return this.node ? this.node.links : [];
  }
  get index() {
    const links = [];
    this.links.forEach(link => {

      links[link.name] = link;
    });
    return links.length > 0 ? links : [];
  }
  constructor({ipfs}) {
    super();
    this.announceBlock = this.announceBlock.bind(this);

    this.chain = chain;
    this.ipfs = ipfs;
  }

  init() {
    return new Promise(async (resolve, reject) => {
      try {
        await this.ipfs.pubsub.subscribe('block-added', this.announceBlock);
        const { hash } = await longestChain();
        this.name = hash || await localDAGMultiaddress();
        this.node = await this.get(this.link);
        log(`Running on the ${network} network`);
        await this.loadChain();
      } catch (error) {
        // TODO: finishe the genesis module
        if (genesis) {
          log(`Creating genesis block on the ${network} network`);
          await this.newDAGChain();
        } else {
          reject(error)
        }
      }
    });
  }
  async resolve(name) {
    return await this.ipfs.name.resolve(name, {recursive: true});
  }

  async get(multihash) {
    return await this.ipfs.object.get(multihash);
  }

  async put(DAGNode) {
    return await this.ipfs.object.put(DAGNode);
  }

  async pin(multihash) {
    return new Promise(async (resolve, reject) => {
      try {
        await this.ipfs.pin.add(multihash);
        resolve();
      } catch (e) {
        reject(e)
      }
    });
  }

  /**
   * addLink
   */
  async addLink(multihash, link) {
    const newDAGChain = await this.ipfs.object.patch.addLink(multihash, link);
    return await this.publish(encode(newDAGChain.multihash));
  }

  async lastLink() {
    try {
      await this.sync();
      const height = Number(this.links.length) - 1;
      for (const link of this.links) {
        if (Number(link.name) === height) {
          return link.multihash;
        }
      }
    } catch (e) {
      console.error('Sync Error::', e);
    }
  }
  // TODO: decentralize
  async lastBlock() {
    const lastLink = await this.lastLink();
    return await new DAGBlock(lastLink);
  }

  /**
   * resolves to the latest chainObject
   */
  async sync() {
    const { hash } = await longestChain();
    this.name = hash || await localDAGMultiaddress();
    this.node = await this.get(this.link);
    return this.link;
  }

  async publish(multihash) {
    const published = await this.ipfs.name.publish(multihash);
    // if (this.name) {
    //   await this.sync();
    // }
    await this.pin(published['value']);
    return published['name']  // only needed when creating genesis block
  }

  async resolveBlocks(multihash, index) {
    try {
      let block = await new DAGBlock(encode(multihash));
      if (block.index > index) {
        await this.pin(encode(multihash)); // pin block locally
        console.log(`added block: ${block.index}  ${block.hash}`);
      }
      chain[block.index] = block;
      console.log(`loaded block: ${block.index}  ${block.hash}`);
      if (block.prevHash && block.prevHash.length > 3) {
        return this.resolveBlocks(Buffer.from(`1220${block.prevHash}`, 'hex'), index);
      }
      return;
    } catch (e) {
      console.error(e);
    }
  }

  async syncChain() {
    try {
      bus.emit('syncing', true);
      if (this.index) {
        const { index, prevHash } = await this.localBlock();
        const height = this.index.length - 1;
        let syncCount = height - index;
        const multihash = this.index[height].multihash;
        log(`syncing ${syncCount} blocks`)
        await this.resolveBlocks(multihash, index);
        await this.updateLocals(multihash.toString('hex'), height, this.link);
        await this.publish(this.link);
      }
      bus.emit('syncing', false);
    } catch (e) {
      console.error('syncChain', e);
    }

  }

  async localBlock() {
    try {
      const CID = await read(localCurrent, 'string'); // read local chain state
      const multihash = encode(new Buffer(CID, 'hex'));
      const current = await this.get(multihash);
      const { index } = JSON.parse(current.data.toString());
      debug(`current local block CID ${CID}`);
      debug(`current local dag: ${multihash}`);
      return {
        index,
        multihash,
        CID
      }
    } catch (e) {
      const CID = await this.get(encode(new Buffer(genesisCID, 'hex')));
      await write(localCurrent, CID.multihash.toString('hex'));
      return await this.localBlock();
    }
  }

  async loadChain() {
    await this.syncChain();
    bus.emit('ready', true);
  }

  addBlock(block) {
    return new Promise(async (resolve, reject) => {
      log(`add block: ${block.index}  ${block.hash}`);
      chain[block.index] = block;
      bus.emit('block-added', block);
      await this.sync();
      await this.pin(multihashFromHex(block.hash)); // pin block locally
      await this.updateLocals(`1220${block.hash}`, block.index, this.link);

      try {
        await this.publish(this.link);
      } catch (e) {
        console.warn(e);
      }

      block.transactions.forEach(transaction => {
        const index = mempool.indexOf(transaction)
        mempool.splice(index)
      })
    });
  }
  // TODO: write using ipfs.files.write
  writeLocals(CID, index, DAGAdress) {
    return new Promise(async (resolve, reject) => {
      await write(localIndex, JSON.stringify(index));
      await write(localCurrent, CID);
      await write(localDAGAddress, DAGAdress);
      resolve();
    });
  }

  updateLocals(CID, height, DAGAdress) {
    return new Promise(async (resolve, reject) => {
      try {
        const index = await read(localIndex, 'json');
        index.push([height, CID]);
        await this.writeLocals(CID, index, DAGAdress);
      } catch (error) {
        await this.writeLocals(CID, [[height, CID]], DAGAdress);
      }
      resolve();
    });
  }

  /**
   * Initialize a new chain on the IPFS network
   * Creates creates & saves the genesisBlock to IPFS, blocks are pinned so they aren't removeable on the local side.
   *
   * @param {object} block The genesis block to write
   * @setup PART of Easy setup your own blockchain, more info URL...
   */
  async newDAGChain() {
    try {
      const genesisDAGNode = await newGenesisDAGNode(difficulty());
      const block = await this.put(genesisDAGNode);
      // await this.pin(encode(block.multihash));
      const chainDAG = await this.ipfs.object.new('unixfs-dir');
      const height = JSON.parse(genesisDAGNode.data.toString()).index;
      const newDAGChain = await this.ipfs.object.patch.addLink(chainDAG.multihash, {name: height, size: block.size, multihash: block.multihash});
      const CID = block.multihash.toString('hex'); // The CID as an base58 string
      await this.updateLocals(CID, 0, encode(newDAGChain.multihash))
      succes('genesisBlock created');
      log(`genesisBlock: ${block.data.toString()}`);
      log(`genesis: ${encode(block.data)}\nCID:\t${CID}`);
      log(`DAGChain name ${encode(newDAGChain.multihash)}`);
      return;
    } catch (e) {
      console.error(e);
    }
  }

  async updateLocalChain(block) {
    const dagnode = await new DAGBlock().put(block);
    await this.addLink(this.link, {name: block.index, size: dagnode.size, multihash: dagnode.multihash});
    return;
  }

  // TODO: go with previous block instead off lastBlock
  // TODO: validate on sync ...
  async announceBlock({data, from}) {
      const block = JSON.parse(data.toString());
