const ipfs = new IPFS();

export default class Miner extends StoreHandler {

  get donationAddress() {
    return 'cpc';
  }

  constructor(address, intensity, autostart) {
    // TODO: limit intensity when pool is empty
    super();
    this.workerPath = join(__dirname, 'miner-worker.js')
    if (!address) {
      MinerWarning('All profit will be donated until address is set');
    }
    this.address = address;
    this.running = 0;


    if (autostart) {
      this.start();
    }
  }

  /**
   * keep node(s) in sync
   */
  onBlockAdded() {
    return new Promise((resolve, reject) => {
      this._onBlockAdded = block => {
        bus.removeListener('block-added', this._onBlockAdded);
        bus.removeListener('invalid-block', this._onBlockInvalid);
        this.mineStop()
        resolve(block);
      }
      this._onBlockInvalid = block => {
        bus.removeListener('block-added', this._onBlockAdded);
        bus.removeListener('invalid-block', this._onBlockInvalid);
        this.mineStop()
        resolve(null);
      }
      bus.once('block-added', this._onBlockAdded);
      bus.once('invalid-block', this._onBlockInvalid);
    });
  }


  async start() {
    // ipfs.pubsub.subscribe('invalid-block');
    this.mining = true;
    this.mine(Math.random().toString(36).slice(-11));
  }

  stop() {
    this.mining = false;
    this.mineStop();
  }

  async mine(job, lastValidBlock) {
    const address = this.address || this.donationAddress;
    const start = Date.now();
    const {block, hashes, index} = await this.mineBlock(difficulty(), address, job);

    if (hashes) {
      const now = Date.now();
      const seconds = (now - start) / 1000;
      const rate = (hashes / seconds) / 1000;
      bus.emit('hashrate', {uid: job, hashrate: (Math.round(rate * 100) / 100)});
    }

    if (block) {
      ipfs.pubsub.publish('block-added', new Buffer(JSON.stringify(block)));
      console.log(`${job}::Whooooop mined block ${block.index}`);
      if (this.mining) {
        await this.onBlockAdded();
        this.mine(job, block);
      }
    } else {
      console.log(`${job}::cancelled mining block ${index}`);
      if (this.mining) this.mine(job);
    }

  }

  /**
   * Mine a block in separate process
   *
   * @param transactions Transactions list to add to the block
   * @param lastBlock Last block in the blockchain
   * @param difficulty Current difficulty
   * @param address Addres for reward transaction
   * @return {*}
   */
  async mineBlock(difficulty, address, job) {
    const block = await nextBlock(address);
    console.log(`${job}::Started mining block ${block.index}`);

    return this.findBlockHash(block, difficulty);
  }

  /**
   * Find block hash according to difficulty
   *
   * @param block
   * @param difficulty
   * @return {Promise}
   */
  findBlockHash (block, difficulty) {
    return new Promise((resolve, reject) => {
      const worker = fork(this.workerPath);
      /*
       * Create worker to find hash in separate process
       */


       /*
        * Hadnle events to stop mining when needed
        */
      this.mineStop = () => {
       removeListeners()
       worker.kill('SIGINT')
       resolve({block: null, hashCount: null, index: block.index});
      }

      // Listeners for stopping mining
      const blockAddedListener = b => {
        if (b.index >= block.index) this.mineStop()
      }
      const mineStopListener = b => this.mineStop
      const removeListeners = () => {
       bus.removeListener('block-added', blockAddedListener)
       bus.removeListener('mine-stop', mineStopListener)
      }
      // If other process found the same block faster, kill current one
      bus.once('block-added', blockAddedListener)
      bus.once('mine-stop', mineStopListener)
      // const result = await minerWorker({block, difficulty})
      worker.on('message', (data) => {
        removeListeners();

        resolve({block: data.block, hashes: data.hashCount});
        worker.kill('SIGINT');
      })
      worker.send({block, difficulty});

    });
  }

}

import { encode, decode } from 'bs58';
import HDWallet from './hd-wallet';
import bigi from 'bigi';

export default class MultiSignature {
	set encoded(value) {
		this._encoded = value;
	}

	set decoded(value) {
		this._decoded = value;
	}

	get encoded() {
		return this._encoded || this.encode();
	}

	get decoded() {
		return this._decoded || this.decode();
	}

	constructor(signature, version = 0x00, multiCodec) {
		if (typeof signature === 'string') {
			this.encoded = signature;
		} else if (typeof signature === 'object') {
			this.decoded = {
				r: signature.r,
				s: signature.s
			};
		}
		this.version = version;
		this.multiCodec = multiCodec;
	}

	export() {
		return this.encoded;
	}

	import() {
		this.decode();
	}

	encode() {
		const buffer = Buffer.concat([
			Buffer.from(this.version.toString()),
			Buffer.from(this.multiCodec.toString()),
			this.decoded.r.toBuffer(),
			this.decoded.s.toBuffer()
		]);
		return encode(buffer);
	}

	decode() {
		const buffer = decode(this.encoded);
		const version = parseInt(buffer.slice(0, 1));
		const codec = parseInt(buffer.slice(1, 5));
		const signature = buffer.slice(5, buffer.length);
		if (version !== this.version) throw TypeError('Invalid version');
		if (this.multiCodec !== codec) throw TypeError('Invalid multiCodec');
  	const r = signature.slice(0, signature.length / 2);
  	const s = signature.slice((signature.length / 2), signature.length);
  	return {
			r: bigi.fromBuffer(r),
			s: bigi.fromBuffer(s)
		};
	}
}

import { encode, decode } from 'bs58';
import HDWallet from './hd-wallet';
import MultiSignature from 'multi-signature';

export default class MultiHDWallet extends HDWallet {
	constructor(network, hdnode) {
		super(network, hdnode);
		this.multiCodec = this.network.multiCodec;
		this.version = 0x00;
	}

	get privateKey() {
		return this.save();
	}

	get publicKeyBuffer() {
		return this.hdnode.neutered().getPublicKeyBuffer();
	}

	get publicKey() {
		return this.hdnode.neutered().toBase58();
	}

	get address() {
		return this.hdnode.getAddress();
	}

	export() {
		return this.encode();
	}

	import(multiWIF) {
		const buffer = this.decode(multiWIF);
		this.load(encode(buffer));
	}

	encode() {
		const base58 = this.save();
		const buffer = Buffer.concat([
			Buffer.from(this.version.toString()),
			Buffer.from(this.multiCodec.toString()),
			Buffer.from(decode(base58))
		]);
		return encode(buffer);
	}

	decode(base58) {
		const buffer = decode(base58);
		const version = parseInt(buffer.slice(0, 1));
		const codec = parseInt(buffer.slice(1, 5));
		const data = buffer.slice(5, buffer.length);
		if (version !== this.version) throw TypeError('Invalid version');
		if (this.multiCodec !== codec) throw TypeError('Invalid multiCodec');
		return data;
	}

	sign(hash, address) {
		if (!address) {
			address = this.address;
		}
		return new MultiSignature(this.version, this.network.multiCodec)
			.sign(hash, address);
	}

	verify(multiSignature, hash, address) {
		return new MultiSignature(this.version, this.network.multiCodec)
			.verify(multiSignature, hash, address);
	}

	derive(path) {
		return new MultiHDWallet(this.network, this.hdnode.derivePath(path));
	}
}

