	decode(buffer) {
		// this.encoded = data;
		this.decoded = decode(buffer || this.buffer);
		this.deserialized = this.deserialize();
		this.decoded = decode(this.buffer);
		return this.decoded;
	}

	serialize({ masterKey, index, depth, parentFingerprint, version, publicKey }) {
		const buffer = Buffer.allocUnsafe(78);
		// update buffer with version > depth > parentFingerprint >
		buffer.writeUInt32(version, 0);
		buffer.writeUInt8(depth, 4);
		buffer.writeUInt32BE(parentFingerprint, 5);
		buffer.writeUInt32BE(index, 9);
		if (masterKey) {
			buffer.writeUInt8(0, 45);
			masterKey.copy(buffer, 46);
		} else {
			publicKey.copy(buffer, 45);
		}
		return buffer;
	}

	infoFromBuffer(buffer) {
		return {
			version: buffer.readUInt32BE(0),
			depth: buffer[4],
			index: buffer.readUInt32BE(9),
			chainCode: buffer.slice(13, 45),
			parentFingerprint: buffer.readUInt32BE(5)
		};
	}

	deserialize(buffer) {
		try {
			const network = validateNetwork(network);
			if (buffer.length !== 78) throw Error('Invalid buffer length');
			const { bip32 } = network;
			const {
				version, index, depth, parentFingerprint, chainCode
			} = this.infoFromBuffer(buffer);
			// validate
			if (version !== bip32.private && version !== bip32.public)
				throw TypeError('invalid network version');

			if (depth === 0 && parentFingerprint !== PARENT_FINGERPRINT)
				throw TypeError('Invalid parent fingerprint');

			if (depth === 0 && index !== 0)
				throw TypeError('Invalid index');

			if (version === bip32.private) {
				if (buffer.readUInt8(45) !== 0x00)
					throw TypeError('Invalid private key');

				const masterKey = buffer.slice(46, 78);
				// cleanup unrelated key
				return {
					masterKey,chainCode, network, depth, index, parentFingerprint
				};
			} else {
				const publicKey = buffer.slice(45, 78);
				// cleanup unrelated key

				return {
					publicKey, chainCode, network, depth, index, parentFingerprint
				};
			}
		} catch (e) {
			throw e;
		}
	}
}

